import React from 'react';
import { useCart } from '../context/CartContext';

const CartPage = () => {
  const { cart } = useCart();  // Access the cart context

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => {
      return total + (item.price || 0);  // Ensure each item has a valid price
    }, 0);
  };

  const totalPrice = calculateTotalPrice();  // Get the total price of all items in the cart

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.map((item, index) => (
          <div key={index} className="cart-item">
            <p>{item.name}</p>
            <p>Price: ${item.price}</p> {/* Display individual item price */}
          </div>
        ))}
      </div>
      <div className="total-price">
        <h3>Total Price: ${totalPrice.toFixed(2)}</h3> {/* Display the total price */}
      </div>
    </div>
  );
};

export default CartPage;
