// src/pages/Fawry.js
import React, { useState } from 'react';
import '../pages/PaymentPage.css';  // Import the common CSS file

const Fawry = () => {
  const [code, setCode] = useState('');

  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  const handleSubmit = () => {
    alert(`Payment with Fawry code: ${code}`);
    // Implement further logic for Fawry payment
  };

  return (
    <div className="payment-page">
      <h2>Fawry Payment</h2>
      <img src="/images/fawry.png" alt="Fawry" className="payment-image" />
      <p>Enter the Fawry payment code you received to complete your transaction.</p>
      <input 
        type="text" 
        value={code} 
        onChange={handleCodeChange} 
        placeholder="Enter Fawry Code" 
      />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default Fawry;
