import React from 'react';
import './JacketsPage.css'; // Import the CSS file for styling
import { useFavorites } from '../context/FavoritesContext';
import { useCart } from '../context/CartContext';
import PopupMessage from '../components/PopupMessage'; // Import the PopupMessage

const jackets = [
  { id: 1, name: 'Bonneville Leather Jacket', price: 99.99, image: '/images/jacket1.png' },
  { id: 2, name: 'Schott NYC Waxy Cowhide Leather Moto Jacket', price: 129.99, image: '/images/jacket2.png' },
];

function JacketsPage() {
  const { addToFavorites, removeFromFavorites, favorites } = useFavorites();
  const { addToCart, removeFromCart, cart } = useCart();

  // Function to check if the product is in the cart
  const isInCart = (jacket) => cart.some(item => item.id === jacket.id);

  // Function to check if the product is in the favorites
  const isInFavorites = (jacket) => favorites.some(item => item.id === jacket.id);

  return (
    <div className="jackets-page">
      <h2>Explore Our Jackets</h2>
      <div className="jackets-list">
        {jackets.map((jacket) => (
          <div className="jacket-card" key={jacket.id}>
            <img src={jacket.image} alt={jacket.name} />
            <h3>{jacket.name}</h3>
            <p>${jacket.price.toFixed(2)}</p>
            <div className="button-group">
              {/* Add/Remove Cart Button */}
              <button
                className={`add-to-cart-btn ${isInCart(jacket) ? 'added' : ''}`}
                onClick={() => isInCart(jacket) ? removeFromCart(jacket) : addToCart(jacket)}
              >
                {isInCart(jacket) ? 'Remove from Cart' : 'Add to Cart'}
              </button>

              {/* Add/Remove Favorites Button */}
              <button
                className={`add-to-favorite-btn ${isInFavorites(jacket) ? 'added' : ''}`}
                onClick={() => isInFavorites(jacket) ? removeFromFavorites(jacket) : addToFavorites(jacket)}
              >
                {isInFavorites(jacket) ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Show Popup Messages */}
      {cart.popup && <PopupMessage message={cart.popup.message} type={cart.popup.type} />}
      {favorites.popup && <PopupMessage message={favorites.popup.message} type={favorites.popup.type} />}
    </div>
  );
}

export default JacketsPage;
