// src/components/PaymentMethodSelection.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './PaymentMethodSelection.css';  // Add CSS for styling the selection page

const PaymentMethodSelection = () => {
  const navigate = useNavigate();

  const handlePaymentMethodSelection = (method) => {
    if (method === 'fawry') {
      navigate('/fawry');
    } else if (method === 'digital-wallet') {
      navigate('/digital-wallet');
    } else if (method === 'credit-card') {
      navigate('/credit-card');
    }
  };

  return (
    <div className="payment-method-selection">
      <h2>Select Payment Method</h2>
      <div className="payment-options">
        <button className="payment-button" onClick={() => handlePaymentMethodSelection('fawry')}>
          <img src="/images/fawry-logo.png" alt="Fawry" className="payment-image" />
          <span>Fawry</span>
        </button>
        <button className="payment-button" onClick={() => handlePaymentMethodSelection('digital-wallet')}>
          <img src="/images/digital-wallet-logo.png" alt="Digital Wallet" className="payment-image" />
          <span>Digital Wallet</span>
        </button>
        <button className="payment-button" onClick={() => handlePaymentMethodSelection('credit-card')}>
          <img src="/images/credit-card-logo.png" alt="Credit Card" className="payment-image" />
          <span>Credit Card</span>
        </button>
      </div>
    </div>
  );
};

export default PaymentMethodSelection;
