import React, { useState } from 'react';
import './DigitalWallet.css';

function DigitalWallet() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Phone Number: ${phoneNumber}, Verification Code: ${verificationCode}`);
    // Handle Digital Wallet payment logic here
  };

  return (
    <div className="digital-wallet-payment">
      <h2>Pay with Digital Wallet</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="phone-number">Enter Phone Number</label>
          <input
            type="tel"
            id="phone-number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="verification-code">Enter Verification Code</label>
          <input
            type="text"
            id="verification-code"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
            required
          />
        </div>
        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
}

export default DigitalWallet;
