import React from 'react';
import './TrousersPage.css';  // Import the CSS file for styling
import { useFavorites } from '../context/FavoritesContext';
import { useCart } from '../context/CartContext';
import PopupMessage from '../components/PopupMessage'; // Import the PopupMessage

const trousers = [
  { id: 1, name: 'Acid Brown Colossus Baggy Jeans', price: 49.99, image: '/images/jeans1.png' },
  { id: 2, name: 'Jaded London Colossus Baggy Jeans', price: 69.99, image: '/images/jeans2.png' },
];

function TrousersPage() {
  const { addToFavorites, removeFromFavorites, favorites } = useFavorites();
  const { addToCart, removeFromCart, cart } = useCart();

  const isInCart = (trouser) => cart.some(item => item.id === trouser.id);
  const isInFavorites = (trouser) => favorites.some(item => item.id === trouser.id);

  return (
    <div className="trousers-page">
      <h2>Explore Our Trousers</h2>
      <div className="trousers-list">
        {trousers.map((trouser) => (
          <div className="trouser-card" key={trouser.id}>
            <img src={trouser.image} alt={trouser.name} />
            <h3>{trouser.name}</h3>
            <p>${trouser.price.toFixed(2)}</p>
            <div className="button-group">
              {/* Add/Remove Cart Button */}
              <button
                className={`add-to-cart-btn ${isInCart(trouser) ? 'added' : ''}`}
                onClick={() => isInCart(trouser) ? removeFromCart(trouser) : addToCart(trouser)}
              >
                {isInCart(trouser) ? 'Remove from Cart' : 'Add to Cart'}
              </button>

              {/* Add/Remove Favorites Button */}
              <button
                className={`add-to-favorite-btn ${isInFavorites(trouser) ? 'added' : ''}`}
                onClick={() => isInFavorites(trouser) ? removeFromFavorites(trouser) : addToFavorites(trouser)}
              >
                {isInFavorites(trouser) ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Show Popup Messages */}
      {cart.popup && <PopupMessage message={cart.popup.message} type={cart.popup.type} />}
      {favorites.popup && <PopupMessage message={favorites.popup.message} type={favorites.popup.type} />}
    </div>
  );
}

export default TrousersPage;
