import React, { useState } from 'react';
import { useCart } from '../context/CartContext'; // Import CartContext
import { useFavorites } from '../context/FavoritesContext'; // Import FavoritesContext
import './ProductCard.css';

function ProductCard({ product }) {
  const { cart, addToCart, removeFromCart } = useCart(); // Using CartContext
  const { favorites, addToFavorites, removeFromFavorites } = useFavorites(); // Using FavoritesContext
  const [isInCart, setIsInCart] = useState(cart.some(item => item.id === product.id));
  const [isInFavorites, setIsInFavorites] = useState(favorites.some(item => item.id === product.id));

  const handleAddToCart = () => {
    if (isInCart) {
      removeFromCart(product.id);
    } else {
      addToCart(product);
    }
    setIsInCart(!isInCart);
  };

  const handleAddToFavorites = () => {
    if (isInFavorites) {
      removeFromFavorites(product.id);
    } else {
      addToFavorites(product);
    }
    setIsInFavorites(!isInFavorites);
  };

  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} />
      <h3>{product.name}</h3>
      <p>${product.price.toFixed(2)}</p>
      <button
        className={`add-to-cart-btn ${isInCart ? 'in-cart' : ''}`}
        onClick={handleAddToCart}
      >
        {isInCart ? 'Remove from Cart' : 'Add to Cart'}
      </button>
      <button
        className={`add-to-favorites-btn ${isInFavorites ? 'in-favorites' : ''}`}
        onClick={handleAddToFavorites}
      >
        {isInFavorites ? 'Remove from Favorites' : 'Add to Favorites'}
      </button>
    </div>
  );
}

export default ProductCard;
