import React from 'react';
import './PopupMessage.css'; // Ensure this CSS is correct

const PopupMessage = ({ message, type }) => {
  if (!message) return null; // Don't show the popup if there is no message

  return (
    <div className={`popup-message ${type}`}>
      {message}
    </div>
  );
};

export default PopupMessage;
