// src/pages/PaymentPage.js
import React from 'react';
import { Link } from 'react-router-dom';
import './PaymentPage.css'; // Reuse styling for the payment page

const PaymentPage = () => {
  return (
    <div className="payment-page">
      <h2>Select Payment Method</h2>
      <div className="payment-method-options">
        <Link to="/fawry" className="payment-option-link">
          <img src="/images/fawry-logo.png" alt="Fawry" />
          <p>Pay with Fawry</p>
        </Link>
        <Link to="/digital-wallet" className="payment-option-link">
          <img src="/images/digital-wallet-logo.png" alt="Digital Wallet" />
          <p>Pay with Digital Wallet</p>
        </Link>
        <Link to="/credit-card" className="payment-option-link">
          <img src="/images/credit-card-logo.png" alt="Credit Card" />
          <p>Pay with Credit Card</p>
        </Link>
      </div>
    </div>
  );
};

export default PaymentPage;
