import React, { useState } from 'react';
import './Fawry.css';

function Fawry() {
  const [code, setCode] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Fawry Code Submitted: ${code}`);
    // Handle Fawry payment logic here
  };

  return (
    <div className="fawry-payment">
      <h2>Pay with Fawry</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="code">Enter Fawry Code</label>
          <input
            type="text"
            id="code"
            value={code}
            onChange={(e) => setCode(e.target.value)}
            required
          />
        </div>
        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
}

export default Fawry;
