import React, { useState } from 'react';

function FawryPage() {
  const [paymentCode, setPaymentCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (paymentCode === '') {
      setError('Please enter a payment code.');
    } else {
      // Handle payment process (e.g., send to backend)
      alert('Fawry payment code submitted!');
    }
  };

  return (
    <div>
      <h2>Fawry Payment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="paymentCode">Enter Fawry Code:</label>
          <input
            type="text"
            id="paymentCode"
            value={paymentCode}
            onChange={(e) => setPaymentCode(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Proceed with Fawry Payment</button>
      </form>
    </div>
  );
}

export default FawryPage;
