function ProductCard({ product, isInCart, handleCartAction }) {
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();

  const handleFavoritesButtonClick = () => {
    if (isInFavorites(product.id)) {
      removeFromFavorites(product.id);
    } else {
      addToFavorites(product);
    }
  };

  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} />
      <h3>{product.name}</h3>
      <p>${product.price.toFixed(2)}</p>

      {/* Cart-specific button */}
      {isInCart ? (
        <button className="cart-btn" onClick={handleCartAction}>
          Remove from Cart
        </button>
      ) : (
        <button className="cart-btn" onClick={() => addToCart(product)}>
          Add to Cart
        </button>
      )}

      {/* Add to Favorites / Remove from Favorites button */}
      <button
        className={`favorites-btn ${isInFavorites(product.id) ? 'in-favorites' : ''}`}
        onClick={handleFavoritesButtonClick}
      >
        {isInFavorites(product.id) ? 'Remove from Favorites' : 'Add to Favorites'}
      </button>
    </div>
  );
}
export default ProductCard; // Ensure this export is there
