// src/context/FavoritesContext.js
import React, { createContext, useContext, useState } from 'react';
import PopupMessage from '../components/PopupMessage'; // Add this line to import PopupMessage


// Create the context
const FavoritesContext = createContext();

// Hook for consuming the context
export const useFavorites = () => {
  const context = useContext(FavoritesContext);
  if (!context) {
    throw new Error('useFavorites must be used within a FavoritesProvider');
  }
  return context;
};

// Provider to manage favorites state
export const FavoritesProvider = ({ children }) => {
  const [favorites, setFavorites] = useState([]);
  const [popupMessage, setPopupMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  // Add product to favorites
  const addToFavorites = (product) => {
    setFavorites((prevFavorites) => [...prevFavorites, product]);
    setPopupMessage('Added to Favorites Successfully');
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 3000); // Hide popup after 3 seconds
  };

  // Remove product from favorites
  const removeFromFavorites = (productId) => {
    setFavorites((prevFavorites) => prevFavorites.filter((item) => item.id !== productId));
    setPopupMessage('Removed from Favorites');
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 3000); // Hide popup after 3 seconds
  };

  return (
    <FavoritesContext.Provider value={{ favorites, addToFavorites, removeFromFavorites }}>
      {children}
      <PopupMessage message={popupMessage} show={showPopup} onClose={() => setShowPopup(false)} />
    </FavoritesContext.Provider>
  );
};

export default FavoritesContext;
