// src/pages/DigitalWallet.js
import React, { useState } from 'react';
import '../pages/PaymentPage.css';  // Correct the path to import the CSS

const DigitalWallet = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [code, setCode] = useState('');

  const handlePhoneNumberChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  const handleSubmit = () => {
    alert(`Payment with Digital Wallet. Phone: ${phoneNumber}, Code: ${code}`);
    // Proceed with payment logic
  };

  return (
    <div className="payment-page">
      <h2>Digital Wallet Payment</h2>
      <p>Enter your phone number and the code sent to you.</p>
      <input 
        type="tel" 
        value={phoneNumber} 
        onChange={handlePhoneNumberChange} 
        placeholder="Enter Phone Number" 
      />
      <input 
        type="text" 
        value={code} 
        onChange={handleCodeChange} 
        placeholder="Enter Verification Code" 
      />
      <button onClick={handleSubmit} className="submit-btn">Submit</button>
    </div>
  );
};

export default DigitalWallet;
