import React from 'react';
import './LaptopsPage.css'; // Import the CSS file for styling
import { useFavorites } from '../context/FavoritesContext';
import { useCart } from '../context/CartContext';

const laptops = [
  { id: 1, name: 'Asus ROG Strix G15 15.6', price: 999.99, image: '/images/laptop2.png' },
  { id: 2, name: 'Asus ROG Strix G513QC-HF002T 15.6', price: 1299.99, image: '/images/laptop1.png' },
];

function LaptopsPage() {
  const { addToFavorites, removeFromFavorites, popup: favoritesPopup } = useFavorites();
  const { addToCart, removeFromCart, popup: cartPopup } = useCart();

  return (
    <div className="laptops-page">
      <h2>Explore Our Laptops</h2>
      <div className="laptops-list">
        {laptops.map((laptop) => (
          <div className="laptop-card" key={laptop.id}>
            <img src={laptop.image} alt={laptop.name} />
            <h3>{laptop.name}</h3>
            <p>${laptop.price.toFixed(2)}</p>
            <div className="button-group">
              <button className="add-to-cart-btn" onClick={() => addToCart(laptop)}>
                Add to Cart
              </button>
              <button className="remove-from-cart-btn" onClick={() => removeFromCart(laptop)}>
                Remove from Cart
              </button>
              <button className="add-to-favorite-btn" onClick={() => addToFavorites(laptop)}>
                Add to Favorites
              </button>
              <button className="remove-from-favorite-btn" onClick={() => removeFromFavorites(laptop)}>
                Remove from Favorites
              </button>
            </div>
          </div>
        ))}
      </div>
      {/* Show Popup Messages */}
      {cartPopup && <PopupMessage message={cartPopup.message} type={cartPopup.type} />}
      {favoritesPopup && <PopupMessage message={favoritesPopup.message} type={favoritesPopup.type} />}
    </div>
  );
}

export default LaptopsPage;
