// src/pages/CreditCard.js
import React, { useState } from 'react';
import '../pages/PaymentPage.css';  // Import the common CSS file

const CreditCard = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');

  const handleCardNumberChange = (e) => {
    setCardNumber(e.target.value);
  };

  const handleExpiryDateChange = (e) => {
    setExpiryDate(e.target.value);
  };

  const handleCvvChange = (e) => {
    setCvv(e.target.value);
  };

  const handleSubmit = () => {
    alert(`Payment with Credit Card: ${cardNumber}, Expiry: ${expiryDate}, CVV: ${cvv}`);
    // Implement further logic for Credit Card payment
  };

  return (
    <div className="payment-page">
      <h2>Credit Card Payment</h2>
      <p>Enter your credit card details to complete the payment.</p>
      <input 
        type="text" 
        value={cardNumber} 
        onChange={handleCardNumberChange} 
        placeholder="Enter Card Number" 
      />
      <input 
        type="text" 
        value={expiryDate} 
        onChange={handleExpiryDateChange} 
        placeholder="Enter Expiry Date (MM/YY)" 
      />
      <input 
        type="text" 
        value={cvv} 
        onChange={handleCvvChange} 
        placeholder="Enter CVV" 
      />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default CreditCard;
