import React from 'react';
import './LaptopsPage.css'; // Import the CSS file for styling
import { useFavorites } from '../context/FavoritesContext';
import { useCart } from '../context/CartContext'; // Import useCart
import PopupMessage from '../components/PopupMessage'; // Import the PopupMessage

const laptops = [
  { id: 1, name: 'Asus ROG Strix G15 15.6', price: 999.99, image: '/images/laptop2.png' },
  { id: 2, name: 'Asus ROG Strix G513QC-HF002T 15.6', price: 1299.99, image: '/images/laptop1.png' },
];

function LaptopsPage() {
  const { addToFavorites, removeFromFavorites, favorites } = useFavorites();
  const { addToCart, removeFromCart, cart } = useCart();

  // Function to check if the product is in the cart
  const isInCart = (laptop) => cart.some(item => item.id === laptop.id);

  // Function to check if the product is in the favorites
  const isInFavorites = (laptop) => favorites.some(item => item.id === laptop.id);

  return (
    <div className="laptops-page">
      <h2>Explore Our Laptops</h2>
      <div className="laptops-list">
        {laptops.map((laptop) => (
          <div className="laptop-card" key={laptop.id}>
            <img src={laptop.image} alt={laptop.name} />
            <h3>{laptop.name}</h3>
            <p>${laptop.price.toFixed(2)}</p>
            <div className="button-group">
              {/* Add/Remove Cart Button */}
              <button
                className="add-to-cart-btn"
                onClick={() => isInCart(laptop) ? removeFromCart(laptop) : addToCart(laptop)}
              >
                {isInCart(laptop) ? 'Remove from Cart' : 'Add to Cart'}
              </button>

              {/* Add/Remove Favorites Button */}
              <button
                className="add-to-favorite-btn"
                onClick={() => isInFavorites(laptop) ? removeFromFavorites(laptop) : addToFavorites(laptop)}
              >
                {isInFavorites(laptop) ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Show Popup Messages */}
      {cart.popup && <PopupMessage message={cart.popup.message} type={cart.popup.type} />}
      {favorites.popup && <PopupMessage message={favorites.popup.message} type={favorites.popup.type} />}
    </div>
  );
}

export default LaptopsPage;
