import React from 'react';
import './MobilePhonesPage.css'; // Keep your existing CSS for layout
import { useFavorites } from '../context/FavoritesContext';
import { useCart } from '../context/CartContext';
import PopupMessage from '../components/PopupMessage'; // Import the PopupMessage

const mobilePhones = [
  { id: 1, name: 'Iphone 16 Pro Max', price: 1999.99, image: '/images/iphone16promax.png' },
  { id: 2, name: 'Iphone 13 Pro Max', price: 799.99, image: '/images/iphone13promax.png' },
];

function MobilePhonesPage() {
  const { addToFavorites, removeFromFavorites, favorites } = useFavorites();
  const { addToCart, removeFromCart, cart } = useCart();

  // Function to check if the product is in the cart
  const isInCart = (phone) => cart.some(item => item.id === phone.id);

  // Function to check if the product is in the favorites
  const isInFavorites = (phone) => favorites.some(item => item.id === phone.id);

  return (
    <div className="mobile-phones-page">
      <h2>Explore Our Mobile Phones</h2>
      <div className="mobile-phones-list">
        {mobilePhones.map((phone) => (
          <div className="phone-card" key={phone.id}>
            <img src={phone.image} alt={phone.name} />
            <h3>{phone.name}</h3>
            <p>${phone.price.toFixed(2)}</p>
            <div className="button-group">
              {/* Add/Remove Cart Button */}
              <button
                className="add-to-cart-btn"
                onClick={() => isInCart(phone) ? removeFromCart(phone) : addToCart(phone)}
              >
                {isInCart(phone) ? 'Remove from Cart' : 'Add to Cart'}
              </button>

              {/* Add/Remove Favorites Button */}
              <button
                className="add-to-favorite-btn"
                onClick={() => isInFavorites(phone) ? removeFromFavorites(phone) : addToFavorites(phone)}
              >
                {isInFavorites(phone) ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Show Popup Messages */}
      {cart.popup && <PopupMessage message={cart.popup.message} type={cart.popup.type} />}
      {favorites.popup && <PopupMessage message={favorites.popup.message} type={favorites.popup.type} />}
    </div>
  );
}

export default MobilePhonesPage;
