// src/pages/DigitalWallet.js
import React, { useState } from 'react';
import '../pages/PaymentPage.css';  // Import the common CSS file

const DigitalWallet = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');

  const handlePhoneNumberChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  const handleVerificationCodeChange = (e) => {
    setVerificationCode(e.target.value);
  };

  const handleSubmit = () => {
    alert(`Payment with phone number: ${phoneNumber} and verification code: ${verificationCode}`);
    // Implement further logic for Digital Wallet payment
  };

  return (
    <div className="payment-page">
      <h2>Digital Wallet Payment</h2>
      <img src="/images/digital-wallet.png" alt="Digital Wallet" className="payment-image" />
      <p>Enter your phone number and the verification code sent to you.</p>
      <input 
        type="text" 
        value={phoneNumber} 
        onChange={handlePhoneNumberChange} 
        placeholder="Enter Phone Number" 
      />
      <input 
        type="text" 
        value={verificationCode} 
        onChange={handleVerificationCodeChange} 
        placeholder="Enter Verification Code" 
      />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default DigitalWallet;
