// src/components/PaymentMethodSelection.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './PaymentMethodSelection.css'; // Ensure you have the correct CSS file

const PaymentMethodSelection = () => {
  const navigate = useNavigate();

  const handlePaymentMethodSelection = (method) => {
    if (method === 'fawry') {
      navigate('/fawry');
    } else if (method === 'digital-wallet') {
      navigate('/digital-wallet');
    } else if (method === 'credit-card') {
      navigate('/credit-card');
    }
  };

  return (
    <div className="payment-method-selection">
      <h2>Select Payment Method</h2>
      <div className="payment-options">
        <div className="payment-option">
          <img
            src="/images/fawry.png" // Ensure the image is in the correct path in the public directory
            alt="Fawry"
            className="payment-image"
          />
          <button
            className="payment-button"
            onClick={() => handlePaymentMethodSelection('fawry')}
          >
            Fawry
          </button>
        </div>
        <div className="payment-option">
          <img
            src="/images/digital-wallet.png" // Ensure the image is in the correct path in the public directory
            alt="Digital Wallet"
            className="payment-image"
          />
          <button
            className="payment-button"
            onClick={() => handlePaymentMethodSelection('digital-wallet')}
          >
            Digital Wallet
          </button>
        </div>
        <div className="payment-option">
          <img
            src="/images/credit-card.png" // Ensure the image is in the correct path in the public directory
            alt="Credit Card"
            className="payment-image"
          />
          <button
            className="payment-button"
            onClick={() => handlePaymentMethodSelection('credit-card')}
          >
            Credit Card
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentMethodSelection;
