import React from 'react';
import { useCart } from '../context/CartContext'; // Access the context
import './Fawry.css';

function Fawry() {
  const { calculateTotalPrice } = useCart(); // Get the total price calculation function
  const totalPrice = calculateTotalPrice(); // Get the total price

  return (
    <div className="fawry-payment">
      <h2>Pay with Fawry</h2>
      <div className="payment-info">
        <h3>Total Price: ${totalPrice.toFixed(2)}</h3> {/* Display total price */}
      </div>

      <div className="payment-form">
        {/* Fawry payment form */}
        <button>Pay Now</button>
      </div>
    </div>
  );
}

export default Fawry;
