import React from 'react';
import './CartPage.css'; // Import the specific CSS for Cart Page

const CartPage = () => {
  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {/* Cart item 1 */}
        <div className="cart-item">
          <img src="/images/item1.jpg" alt="Item 1" className="cart-item-img" />
          <div className="cart-item-details">
            <h3>Item Name</h3>
            <p>Price: $100</p>
            <button className="remove-item-btn">Remove</button>
          </div>
        </div>

        {/* Cart item 2 */}
        <div className="cart-item">
          <img src="/images/item2.jpg" alt="Item 2" className="cart-item-img" />
          <div className="cart-item-details">
            <h3>Item Name</h3>
            <p>Price: $50</p>
            <button className="remove-item-btn">Remove</button>
          </div>
        </div>

        {/* More items as needed */}
      </div>

      <div className="cart-summary">
        <p>Total: $150</p>
        <button className="checkout-btn">Proceed to Checkout</button>
      </div>
    </div>
  );
};

export default CartPage;
