// src/components/PopupMessage.js
import React from 'react';
import './PopupMessage.css'; // Ensure that PopupMessage.css exists and has the styles

const PopupMessage = ({ message, show, onClose, type }) => {
  if (!show) return null;

  return (
    <div className={`popup-message ${type === 'error' ? 'error' : 'success'}`}>
      <span>{message}</span>
      <button onClick={onClose}>X</button>
    </div>
  );
};

export default PopupMessage;
