import React, { useState } from 'react';

function DigitalWalletPage() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (phoneNumber === '' || verificationCode === '') {
      setError('Please fill in both phone number and verification code.');
    } else {
      // Handle payment verification process (e.g., send to backend)
      alert('Digital Wallet payment submitted!');
    }
  };

  return (
    <div>
      <h2>Digital Wallet Payment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="tel"
            id="phoneNumber"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="verificationCode">Enter Verification Code:</label>
          <input
            type="text"
            id="verificationCode"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Proceed with Digital Wallet Payment</button>
      </form>
    </div>
  );
}

export default DigitalWalletPage;
